    val listData : ArrayList<Films>
        get() {
            val list = arrayListOf<Films>()
            for (position in filmName.indices){
                val films = Films()
                films.name = filmName[position]
                films.sutradara = filmSutradara[position]
                films.produser = filmProduser[position]
                films.rilis = filmRilis[position]
                films.detail = filmDetails[position]
                films.photo = filmImages[position]
                list.add(films)
            }
            return list
        }
